import React from 'react';
import styles from './Form.css';
import axios from 'axios';
import {extendObservable} from 'mobx'
import {observer} from 'mobx-react';




export default observer (class Form extends React.Component  {
    constructor (props) {
        super (props);

        extendObservable(this, {
            login: '',
           password: '',
           classDefault: 'loginForm'
          
        })
    }
    SubmitTo = () => {
        let {login , password} = this;
        console.log(login);
        console.log(password);
        
    }

    onChange = e => {
        let {name, value} = e.target;
        this[name] = value;
    }

    Submit = async () => {
        
        let loginData = {
            username: this.login,
            password: this.password
        }
        try {
            let response = await axios.post('http://195.242.161.52:8888/login', loginData)
            let auth = response.data
            if (auth.Auth == 'Denied'){
                this.classDefault = 'loginFormDenied'
                console.log('htubcnhfwbz ghjikf')
            }
            console.log(auth.Auth)
        } catch (e) {
            console.log(e)
        }
    }
    render() {
        let {login, password,classDefault} = this;
        //let classDefault= 'loginForm'
        return (
            <div className={classDefault}>
                <span>Login</span>
                <div class="form-group">
                    <input type="login" name="login" onChange = {this.onChange} value = {login} class="form-control" id="InputLogin" placeholder="Login" />
                </div>
                <div class="form-group">
                    <input type="password" name="password"onChange = {this.onChange} value = {password} class="form-control" id="InputPassword" placeholder="Password" />
                </div>

                <button onClick={this.SubmitTo} type="submit" class="btn btn-primary">Submit</button>
                <button onClick={this.Submit} type="button" class="btn btn-info">Инфо</button>
                //<div className = {styles.image}></div>
                <div className={styles.image2} ><img src="https://www.jusco-easterntravels.com/images/Successful-icon.png" alt= 'success'/></div>
            </div>
        )
    }
})
