import React from 'react';
import styles from './Form.css';
import axios from 'axios';
import {extendObservable} from 'mobx'
import {observer} from 'mobx-react';



export default observer (class Form extends React.Component  {
    constructor (props) {
        super (props);

        extendObservable(this, {
            login: '',
            password: '',
            authAccess: ''
        })
    }
    SubmitTo = () => {
        let {login , password, authAccess} = this;
        console.log(login);
        console.log(password);
        console.log(authAccess)
    }

    onChange = e => {
        let {name, value} = e.target;
        this[name] = value;
    }

    Submit = async () => {
        
        let loginData = {
            username: this.login,
            password: this.password
        }
        try {
            let response = await axios.post('http://195.242.161.52:8888/login', loginData)
            let auth = response.data
            authAccess = auth.Auth
            console.log(auth)
        } catch (e) {
            console.log(e)
        }
    }
    render() {
        let {login, password} = this;
        return (
            <div className='loginForm'>
                <span>Login</span>
                <div class="form-group">
                    <input type="login" name="login" onChange = {this.onChange} value = {login} class="form-control" id="InputEmail" placeholder="Login" />
                </div>
                <div class="form-group">
                    <input type="password" name="password"onChange = {this.onChange} value = {password} class="form-control" id="InputPassword" placeholder="Password" />
                </div>

                <button onClick={this.SubmitTo} type="submit" class="btn btn-primary">Submit</button>
                <button onClick={this.Submit} type="button" class="btn btn-info">Инфо</button>
            </div>
        )
    }
})
